import { useState } from "react";
import { useNavigate } from "react-router-dom";
import useFetchData from "../components/generics/FetchData";
import { GITHUB_URI } from "../constants/constants";
import "./Home.css";
const Home = () => {
  const [user, setUser] = useState("");
  const [error, setError] = useState(null);
  const [requestUser, setRequestUser] = useState(false);
  const navigate = useNavigate();

  const apiData = useFetchData(`${GITHUB_URI}/users/${user}`, [], requestUser);
  if (requestUser) {
    if (user !== "") {
      navigate(`/${user}`, { state: apiData });
    } else {
      //setError("please enter the user you want to check");
    }
  }
  const searchUser = (newUser) => {
    if (error) {
      //setError(null);
    }
    //setUser(newUser);
  };

  return (
    <div className="home_main">
      <div className="home_title">GITHUB FINDER</div>
      <div className="home_subtitle">
        Check your favorite github user bellow
      </div>
      <div className="home_search">
        <input
          className="home_search_input"
          value={user}
          type="text"
          onChange={(e) => searchUser(e.target.value)}
          placeholder="Search User"
          autoFocus={true}
        />
        <button
          className="home_search_button"
          onClick={() => {
            if (!requestUser) setRequestUser(true);
          }}
        >
          GO
        </button>
      </div>
      <div className="home_error_message">{error}</div>
    </div>
  );
};

export default Home;
