import { useState } from "react";
import useFetchData from "../components/generics/FetchData";
import Pagination from "../components/generics/Pagination";
import { numberOfReposPerPage } from "../constants/constants";

const Repositories = ({ reposUrl, reposNumber }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const fetchData = async () => {
    try {
      const { data: response } = await axios.get(`${path}`);
      setData(response);
    } catch (error) {
      console.error(error);
    }
    setLoading(false);
  };
  useEffect(() => {
    fetchData();
  }, [path]);
  console.log(
    reposUrl + `?page=${currentPage}&per_page=${numberOfReposPerPage}`
  );
  const repoData = useFetchData(
    reposUrl + `?page=${currentPage}&per_page=${numberOfReposPerPage}"`,
    [reposUrl + `?page=${currentPage}&per_page=${numberOfReposPerPage}"`]
  );

  if (repoData.loading) {
    return <div>Loading</div>;
  }

  // Get current page posts
  const indexOfLastPost = currentPage * numberOfReposPerPage;
  const indexOfFirstPost = indexOfLastPost - numberOfReposPerPage;
  const currentPosts = repoData.data.slice(indexOfFirstPost, indexOfLastPost);

  console.log(currentPosts);
  return (
    <>
      <div className="container mt-5">
        {currentPosts.map((post) => (
          <li key={post.id} className="list-group-item">
            {post.name}
            {post.description}
          </li>
        ))}
        <Pagination
          postsPerPage={numberOfReposPerPage}
          totalPosts={reposNumber}
          paginate={(pageNumber) => setCurrentPage(pageNumber)}
        />
      </div>
    </>
  );
};
export default Repositories;
