import { useState } from "react";
import useFetchData from "../apiFetches/FetchData";
import Pagination from "../components/generics/Pagination";

const Repositories = ({ reposUrl }) => {
  const repoData = useFetchData(reposUrl);
  const [postsPerPage] = useState(10);
  const [currentPage, setCurrentPage] = useState(1);
  //Extract only what is required
  console.log(repoData);

  if (repoData.loading) {
    return <div>Loading</div>;
  }

  // Get current posts
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = repoData.data.slice(indexOfFirstPost, indexOfLastPost);

  console.log(currentPosts);
  return (
    <>
      {repoData.loading && <div>Loading</div>}

      <div className="container mt-5">
        {currentPosts.map((post) => (
          <li key={post.id} className="list-group-item">
            {post.name}
            {post.description}
          </li>
        ))}
        <Pagination
          postsPerPage={postsPerPage}
          totalPosts={repoData.data.length}
          paginate={(pageNumber) => setCurrentPage(pageNumber)}
        />
      </div>
    </>
  );
};
export default Repositories;
