const UserCard = (props) => {
  console.log(props);
  const { avatarUrl, name, login, location, bio, followers, following } =
    props.data;
  return (
    <>
      <img className="usercard__avatar" src={avatarUrl} alt={avatar_url} />
      <strong>{name}</strong>
      <p>@{login}</p>
      {location && <p>{location}</p>}
      <p>{bio}</p>
      <p>
        <strong>{followers}</strong> followers
      </p>
      <p>
        <strong>{following}</strong> following
      </p>
    </>
  );
};

export default UserCard;
