import { useState } from "react";
import useFetchData from "../components/generics/FetchData";
import Pagination from "../components/generics/Pagination";
import { numberOfReposPerPage } from "../constants/constants";
import { useParams } from "react-router-dom";
import { GITHUB_URI } from "../constants/constants";

const Repositories = ({ reposUrl, reposNumber }) => {
  const { user } = useParams();
  const [currentPage, setCurrentPage] = useState(1);

  console.log(
    `${GITHUB_URI}/users/${user}?page=${currentPage}&per_page=${numberOfReposPerPage}`
  );
  const repoData = useFetchData(
    reposUrl + `?page=${currentPage}&per_page=${numberOfReposPerPage}"`
  );

  if (repoData.loading) {
    return <div>Loading</div>;
  }

  console.log(reposNumber);
  if (reposNumber === 0) {
    return <div>No Repositories</div>;
  }

  return (
    <>
      <div className="container mt-5">
        {repoData.data.map((post) => (
          <li key={post.id} className="list-group-item">
            {post.name}
            {post.description}
          </li>
        ))}
        <Pagination
          postsPerPage={numberOfReposPerPage}
          totalPosts={reposNumber}
          paginate={(pageNumber) => setCurrentPage(pageNumber)}
        />
      </div>
    </>
  );
};
export default Repositories;
