import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import useFetchData from "../components/generics/FetchData";
import { GITHUB_URI } from "../constants/constants";
import axios from "axios";
import "./Home.css";
const Home = () => {
  const [user, setUser] = useState("");
  const [error, setError] = useState(null);
  const [requestUser, setRequestUser] = useState(false);
  const navigate = useNavigate();

  const [data, setData] = useState({});
  const [loading, setLoading] = useState(true);
  const [error2, setError2] = useState(false);
  const fetchData = async () => {
    try {
      const { data: response } = await axios.get(`${path}`);
      setData(response);
    } catch (error) {
      setError2(true);
    }
    setLoading(false);
  };
  useEffect(() => {
    if (requestUser) fetchData();
  }, [requestUser]);

  return {
    data,
    loading,
    error2,
  };
  const apiData = useFetchData(`${GITHUB_URI}/users/${user}`);

  if (requestUser) {
    if (user !== "") {
      navigate(`/${user}`, { state: { id: 1, name: "sabaoon" } });
    } else {
      setError("please enter the user you want to check");
    }
  }
  const searchUser = (newUser) => {
    if (error) {
      setError(null);
    }
    setUser(newUser);
  };

  return (
    <div className="home_main">
      <div className="home_title">GITHUB FINDER</div>
      <div className="home_subtitle">
        Check your favorite github user bellow
      </div>
      <div className="home_search">
        <input
          className="home_search_input"
          value={user}
          type="text"
          onChange={(e) => searchUser(e.target.value)}
          placeholder="Search User"
          autoFocus={true}
        />
        <button
          className="home_search_button"
          onClick={() => {
            if (!requestUser) setRequestUser(true);
          }}
        >
          GO
        </button>
      </div>
      <div className="home_error_message">{error}</div>
    </div>
  );
};

export default Home;
