import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import useFetchData from "../components/generics/FetchData";
import { GITHUB_URI } from "../constants/constants";
import { Loading } from "../components/generics/Loading";
import "./Home.css";

const Home = () => {
  const [user, setUser] = useState("");
  const error = useRef(null);
  const [requestUser, setRequestUser] = useState(false);

  //should only return here in case of failure or while it is loading
  const fetchResult = useFetchData(
    `${GITHUB_URI}/users/${user}`,
    [requestUser],
    requestUser,
    `/${user}`
  );

  console.log(requestUser);
  console.log(fetchResult);

  const submitUser = () => {
    if (!requestUser) {
      if (user !== "") {
        console.log(1);
        setRequestUser(true);
      } else {
        console.log(2);
        error.current = "please enter the user you want to check";
        setRequestUser(true);
      }
    }
  };

  const searchUser = (newUser) => {
    if (error.current) {
      error.current = null;
    }
    setUser(newUser);
  };

  if (fetchResult.error) return <div>error</div>;
  if (fetchResult.loading) return <Loading />;

  return (
    <div className="home_main">
      <div className="home_title">GITHUB FINDER</div>
      <div className="home_subtitle">
        Check your favorite github user bellow
      </div>
      <div className="home_search">
        <input
          className="home_search_input"
          value={user}
          type="text"
          onChange={(e) => {
            searchUser(e.target.value);
          }}
          placeholder="Search User"
          autoFocus={true}
        />
        <button
          className="home_search_button"
          onClick={() => {
            submitUser();
          }}
        >
          GO
        </button>
      </div>
      <div className="home_error_message">{error.current}</div>
    </div>
  );
};

export default Home;
