import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import useFetchData from "../components/generics/FetchData";
import { GITHUB_URI } from "../constants/constants";
import "./Home.css";
const Home = () => {
  const [user, setUser] = useState("");
  const error = useRef(null);
  const [requestUser, setRequestUser] = useState(false);
  const navigate = useNavigate();

  //should only return here in case of failure or while it is loading
  const fetchResult = useFetchData(
    `${GITHUB_URI}/users/${user}`,
    [],
    requestUser,
    `/${user}`
  );

  const submitUser = () => {
    if (!requestUser) {
      if (user !== "") {
        setRequestUser(true);
      } else {
        error.current = "please enter the user you want to check";
        setRequestUser(false);
      }
    }
  };

  const searchUser = (newUser) => {
    if (error) {
      error.current = null;
    }
    setUser(newUser);
  };

  return (
    <div className="home_main">
      <div className="home_title">GITHUB FINDER</div>
      <div className="home_subtitle">
        Check your favorite github user bellow
      </div>
      <div className="home_search">
        <input
          className="home_search_input"
          value={user}
          type="text"
          onChange={(e) => {
            if (!requestUser) {
              searchUser(e.target.value);
            }
          }}
          placeholder="Search User"
          autoFocus={true}
        />
        <button
          className="home_search_button"
          onClick={() => {
            submitUser();
          }}
        >
          GO
        </button>
      </div>
      <div className="home_error_message">{error}</div>
    </div>
  );
};

export default Home;
